    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
      modelo = (DefaultTableModel) jTable2.getModel();
      modelo.setRowCount(0); // eliminando as linhas da tabela
      
      for(int i = 0; i < Dados.lista_veic.length; i++) { // navegando no vetor (ArrayList)
         // criando um array(vetor) de objetos e adicionando este vetor na linha do modelo (jTable) 
           modelo.addRow(new Object[]{
           Dados.lista_veic[i].getChassi(),
           Dados.lista_veic[i].getPlaca(),
           Dados.lista_veic[i].getMarca(),
           Dados.lista_veic[i].getModelo(),
           Dados.lista_veic[i].gettipo(),
    });
   };
    }//GEN-LAST:event_btnBuscarActionPerformed

